import "./Employees.scss";
import { useState } from "react";
import maleProfile from "../../images/maleProfile.jpg";
import femaleProfile from "../../images/femaleProfile.jpg";

const Employees = () => {
  const [selectedTeam, setTeam] = useState("TeamB");
  const [employees, setEmployees] = useState([
    {
      id: 1,
      fullName: "Bob Jones",
      designation: "JavaScript Developer",
      gender: "male",
      teamName: "TeamA",
    },
    {
      id: 2,
      fullName: "Jill Bailey",
      designation: "Node Developer",
      gender: "female",
      teamName: "TeamA",
    },
    {
      id: 3,
      fullName: "Gail Shepherd",
      designation: "Java Developer",
      gender: "female",
      teamName: "TeamA",
    },
    {
      id: 4,
      fullName: "Sam Reynolds",
      designation: "React Developer",
      gender: "male",
      teamName: "TeamB",
    },
    {
      id: 5,
      fullName: "David Henry",
      designation: "DotNet Developer",
      gender: "male",
      teamName: "TeamB",
    },
    {
      id: 6,
      fullName: "Sarah Blake",
      designation: "SQL Server DBA",
      gender: "female",
      teamName: "TeamB",
    },
    {
      id: 7,
      fullName: "James Bennet",
      designation: "Angular Developer",
      gender: "male",
      teamName: "TeamC",
    },
    {
      id: 8,
      fullName: "Jessica Faye",
      designation: "API Developer",
      gender: "female",
      teamName: "TeamC",
    },
    {
      id: 9,
      fullName: "Lita Stone",
      designation: "C++ Developer",
      gender: "female",
      teamName: "TeamC",
    },
    {
      id: 10,
      fullName: "Daniel Young",
      designation: "Python Developer",
      gender: "male",
      teamName: "TeamD",
    },
    {
      id: 11,
      fullName: "Adrian Jacobs",
      designation: "Vue Developer",
      gender: "male",
      teamName: "TeamD",
    },
    {
      id: 12,
      fullName: "Devin Monroe",
      designation: "Graphic Designer",
      gender: "male",
      teamName: "TeamD",
    },
  ]);

  function handleTeamSelectionChange(event) {
    setTeam(event.target.value);
  }
  function handleEmployeeCardClick(event) {
    const transformedEmployees = employees.map((e) =>
      e.id === parseInt(event.currentTarget.id)
        ? e.teamName === selectedTeam
          ? { ...e, teamName: "" }
          : { ...e, teamName: selectedTeam }
        : e
    );
  }

  return (
    <main className="container ">
      <div className="row justify-content-center my-3">
        <div className="col-6 text-center">
          <select
            className="form-select form-select-lg"
            value={selectedTeam}
            onChange={handleTeamSelectionChange}
          >
            <option value={"TeamA"}>TeamA</option>
            <option value={"TeamB"}>TeamB</option>
            <option value={"TeamC"}>TeamC</option>
            <option value={"TeamD"}>TeamD</option>
          </select>
        </div>
      </div>
      <div className="row justify-content-center my-3">
        <div className="col-8">
          <div className="card-collection">
            {employees.map((e) => (
              <div
                id={e.id}
                onClick={handleEmployeeCardClick}
                className={
                  e.teamName === selectedTeam ? "card m-1 standout" : "card m-1"
                }
              >
                <img
                  src={e.gender === "male" ? maleProfile : femaleProfile}
                  className="card-img-top"
                />
                <div className="card-body">
                  <h5 className="card-title">Full Name: {e.fullName}</h5>
                  <p className="card-text">
                    <b>Designation:</b> {e.designation}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </main>
  );
};

export default Employees;
