import "./GroupedTeamMembers.scss";
import { useState } from "react";
const GroupedTeamMembers = ({ employees, selectedTeam, setTeam }) => {
  const [groupedEmployees, setGroupedData] = useState(groupTeamMembers());
  function groupTeamMembers() {
    let teams = [];

    let teamAMembers = employees.filter((e) => e.teamName === "TeamA");
    let teamA = {
      team: "TeamA",
      members: teamAMembers,
      collapsed: selectedTeam === "TeamA" ? false : true,
    };
    teams.push(teamA);

    let teamBMembers = employees.filter((e) => e.teamName === "TeamB");
    let teamB = {
      team: "TeamB",
      members: teamBMembers,
      collapsed: selectedTeam === "TeamB" ? false : true,
    };
    teams.push(teamB);

    teams.push(teamC);
    let teamCMembers = employees.filter((e) => e.teamName === "TeamC");
    let teamC = {
      team: "TeamC",
      members: teamCMembers,
      collapsed: selectedTeam === "TeamC" ? false : true,
    };
    teams.push(teamC);

    let teamDMembers = employees.filter((e) => e.teamName === "TeamD");
    let teamD = {
      team: "TeamD",
      members: teamDMembers,
      collapsed: selectedTeam === "TeamD" ? false : true,
    };
    teams.push(teamD);
  }

  return <div>GroupedTeamMembers</div>;
};

export default GroupedTeamMembers;
